INSERT INTO survivor VALUES (1, 'Wilson', 'The Gentleman Scientist', 'At heart Wilson is truly a man of science. It''s not in his nature to resist the call of the unknown.', 'I will conquer this world with the power of my MIND!', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/Wilson.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/Wilson.png', 'April 23', 'Bacon and Eggs', 'Slim', 150, 150, 200);
INSERT INTO survivor VALUES (2, 'Willow', 'The Firestarter', 'Willow is a candid woman whose penchant for fire often gets her into more than a little trouble.', 'All will bathe in the prettiest of flames.', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/Willow.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/Willow.png', 'May 7', 'Spicy Chili', 'Slim', 150, 150, 120);
INSERT INTO survivor VALUES (3, 'Wolfgang', 'The Strongman', 'Wolfgang is an affable, musclebound giant with a heart plagued by a myriad of phobias.', 'I am mighty! No one is mightier!', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/Wolfgang.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/Wolfgang.png', 'October 2', 'Roast Potato', 'Grim', 200, 200, 200);
INSERT INTO survivor VALUES (4, 'Wendy', 'The Bereaved', 'Wendy is a morose young lady with a sisterly bond so powerful, it transcends death itself.', 'Abigail? Come back! I''m not done playing with you.', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/Wendy.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/Wendy.png', 'November 11', 'Banana Pop', 'Slim', 150, 150, 200);
INSERT INTO survivor VALUES (5, 'WX-78', 'The Soulless Automaton', 'One might assume WX''s callous nature hides a sensitive inner soul. One would assume incorrectly.', 'EMPATHY MODULE NOT RESPONDING', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/WX-78.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/WX-78.png', 'November 28', 'Butter Muffin', 'Grim', 125, 125, 150);
INSERT INTO survivor VALUES (6, 'Wickerbottom', 'The Librarian', 'A woman of curious mind and stern air, Wickerbottom thrives on the lifelong pursuit of knowledge.', 'Shhhh! No talking!', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/Wickerbottom.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/Wickerbottom.png', 'December 6', 'Surf ''n'' Turf', 'Grim', 125, 150, 250);
INSERT INTO survivor VALUES (7, 'Woodie', 'The Lumberjack', 'A gruff, bearded woodsman, Woodie has a soft spot for nature, plaid, and his trusty axe, Lucy.', 'That''s a nice lookin'' tree, eh? Not for long.', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/Woodie.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/Woodie.png', 'September 12', 'Honey Nuggets', 'Slim', 150, 150, 200);
INSERT INTO survivor VALUES (8, 'Wes', 'The Silent', 'Wes is a mime of many thoughts, but few words.', '...', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/Wes.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/Wes.png', 'April 16', 'Fresh Fruit Crepes', 'None', 75, 75, 75);
INSERT INTO survivor VALUES (9, 'Maxwell', 'The Puppet Master', 'Formerly the Nightmare King, lately Maxwell finds himself reacquainted with life among the commonfolk.', 'Freedom suits me.', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/Maxwell.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/Maxwell.png', 'October 22', 'Wobster Dinner', 'Grim', 75, 150, 200);
INSERT INTO survivor VALUES (10, 'Wigfrid', 'The Performance Artist', 'Whoever Wigfrid was before her titular role has been lost to time, and the power of method acting.', 'All the wörld''s a stage. För me!', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/Wigfrid.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/Wigfrid.png', 'July 23', 'Turkey Dinner', 'Slim', 200, 120, 120);
INSERT INTO survivor VALUES (11, 'Webber', 'The Indigestible', 'Webber is a polite young monster who cares deeply for all his friends, human or otherwise.', 'We''re always together, and never alone!', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/Webber.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/Webber.png', 'April 30', 'Ice Cream', 'Grim', 175, 175, 100);
INSERT INTO survivor VALUES (12, 'Winona', 'The Handywoman', 'Winona is a former assembly line worker with a boisterous attitude and a love of all things mechanical.', 'Anything can be fixed with hard work and elbow grease.', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/Winona.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/Winona.png', 'September 13', 'Vegetable Stinger', 'Slim', 150, 150, 100);
INSERT INTO survivor VALUES (13, 'Warly', 'The Culinarian', 'To Warly, cooking is a language through which to express love and gratitude to those he holds dear.', 'Nothing worthwhile is ever done on an empty stomach!', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/Warly.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/Warly.png', 'July 25', 'None', 'Grim', 150, 250, 200);
INSERT INTO survivor VALUES (14, 'Wortox', 'The Soul Starved', 'Wortox is a mischievous imp whose levity masks a deep well of guilt. He has taken many souls in his time.', 'Pardon me if I don''t shake your hand.', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/Wortox.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/Wortox.png', 'March 28', 'Pomegranate', 'Grim', 200, 175, 150);
INSERT INTO survivor VALUES (15, 'Wormwood', 'The Lonesome', 'An amalgam of vines given sentience by lunar magic, Wormwood searches for friends so he can finally belong.', 'Hello friend?', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/Wormwood.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/Wormwood.png', 'June 6', 'Cooked Banana', 'Grim', 150, 150, 200);
INSERT INTO survivor VALUES (16, 'Wurt', 'The Half-Pint', 'Wurt is a curious young Merm trying to expand her horizons... as long as those horizons don''t extend into Pig territory.', 'Mermfolk known for hos-per-tality, florp.', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/Wurt.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/Wurt.png', 'October 17', 'Durian', 'Slim', 150, 200, 150);
INSERT INTO survivor VALUES (17, 'Walter', 'The Fearless', 'Walter is an odd but cheerful boy, whose ability to assess danger leaves something to be desired.', 'A Pinetree Pioneer is always prepared!', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/Walter.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/Walter.png', 'March 31', 'Trail Mix', 'Slim', 130, 110, 200);
INSERT INTO survivor VALUES (18, 'Wanda', 'The Timekeeper', 'Wanda is a skilled clockmaker who will stop at nothing to outrun her future.', 'Time! I just need more time!', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/icons/Wanda.png', 'https://raw.githubusercontent.com/Juan-Kineipe/dont-starve-together/main/dont-starve-together-ui/src/assets/portraits/Wanda.png', 'August 8', 'Taffy', 'Grim', 80, 175, 200);
